import React, { PureComponent } from "react";
import "./header.scss";
import Signout from "../signout/Signout";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSearch } from "@fortawesome/free-solid-svg-icons";
import { Link } from "react-router-dom";
import { faTrash, faPaperPlane, faInbox, faExclamationTriangle, faEdit } from "@fortawesome/free-solid-svg-icons";

import debounce from "lodash/debounce";
import ComposeMessage from "../compose-message/ComposeMessage";

export class Header extends PureComponent {
  constructor(props) {
    super(props);

    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleSearchClick = this.handleSearchClick.bind(this);
    this.performSearch = debounce(this.performSearch.bind(this), 1000);
  }

  handleSearchClick(evt) {
    if (this.props.searhQuery !== "") {
      this.performSearch();
    }
  }

  handleInputChange(evt) {
    this.props.setSearchQuery(evt.target.value);
    this.performSearch();
  }

  performSearch() {
    const searchParams = {}
    if (!this.props.searchQuery || this.props.searchQuery === "") {
      searchParams.labelIds = ["INBOX"];
    }
    this.props.getLabelMessages({ ...searchParams })
  }

  render() {
    // const userInfo = this.props.googleUser.w3;
    // const email = userInfo.U3;
    // const fullName = userInfo.ig;


    return (
      <header className="d-flex p-4 align-content-center align-items-center header">
        <div className="header-logo ">

          <div>
            <Link to='/inbox'>
              <button  >
                <FontAwesomeIcon
                  title="Back"
                  icon={faInbox}
                  size="lg"
                  style={{ 'color': '#337ab7' }}
                />
              </button>
            </Link>
          </div>


          <div>
            <Link to='/trash'>
              <button><FontAwesomeIcon
                title='Trash'
                icon={faTrash}
                size='lg'
                style={{ 'color': '#337ab7' }} />
              </button>
            </Link>
          </div>


          <div>
            <Link to='/sent'>
              <button>
                <FontAwesomeIcon
                  title='Send'
                  icon={faPaperPlane}
                  style={{ 'color': '#337ab7' }}
                  size='lg' />
              </button>
            </Link>
          </div>

          <div>
            <Link to='/spam'>
              <button>
                <FontAwesomeIcon
                  title='Spam'
                  icon={faExclamationTriangle}
                  size='lg'
                  style={{ 'color': '#337ab7' }} />

              </button>
            </Link>
          </div>


          <div className="">
            <div className="">
              <ComposeMessage
                subject=""
                to=""
              >
                <button className=''>
                  <FontAwesomeIcon
                    title="Compose"
                    icon={faEdit}
                    size='lg'
                    style={{ 'color': '#337ab7' }} />
                </button>
              </ComposeMessage>
            </div>
          </div>
        </div>


        <div className="input w-50 ml-1 ">
          <input
            type="search"
            className="form-control border-light"
            placeholder="Search mail"
            value={this.props.searchQuery}
            onChange={this.handleInputChange}
          />
          <div className="input-group-append" onClick={this.handleSearchClick}>
            <button
              className="btn btn-light btn-outline-light bg-white text-dark"
              type="button"
            >
              <FontAwesomeIcon icon={faSearch} />
            </button>
          </div>
        </div>
        <div className='user-name'>

          {/* {fullName} */}
          </div>


        <Signout onSignout={this.props.onSignout} />
      </header>
    );
  }
}

export default Header;
